    </BrowserRouter>
    )
}

  render() {
    return (

      <div className="App">
      <div className="Header">
      { <Header /> }
      { this.state.user ? ( <Home /> ) : ( this.loginPath() ) }
      </div>

        {<Articles />}

      </div>
    );
  }
}

export default App;
