import React, { useState, Component } from 'react';
import fire from './config/Fire';
import {BrowserRouter, Route, NavLink} from 'react-router-dom';
import './App.css';

class Dropdown extends React.Component {

  constructor(props) {
    super(props);
    this.pathToAvatar = this.pathToAvatar.bind(this);
    this.state = {
      avatar_link: this.databaseAvatarLink,
    };

  }



//ДОБАВИТЬ УСЛОВИЕ: ЕСЛИ В БАЗЕ ЕССТЬ ССЫЛКА, ВСТАВЛЯТЬ ЕЕ, ЕСЛИ НЕТ ТО ВСТАВЛЯТЬ СТАНДАРТНУЮ
pathToAvatar(){
  const userId = fire.auth().currentUser.uid;
  fire.database().ref('/users/' + userId).once('value').then((snapshot) => {
    const defaultAvatar = 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/1200px-React-icon.svg.png'
    const databaseAvatarLink = (snapshot.val() && snapshot.val().avatar_link) || (this.setState({avatar_link: defaultAvatar})); 
    this.setState({avatar_link: databaseAvatarLink.toString()});
  });
}

componentDidMount(){
  this.pathToAvatar();
}

render() {

  function   logout() {
    fire.auth().signOut();
  }

  function Navbar(props){
    return(
      <nav className="navbar">
      <div className="navbar-nav">{ props.children }</div>
      </nav>
      )
  }

  function App(props){ 
    return (
      <Navbar>
      <NavItem icon= <img className='Avatar' src= {props} />>
      <div className='Login' onClick = {logout}>Logout</div>
      <NavLink to='/profile'><div className='Login'>Profile</div></NavLink>
      </NavItem>
      </Navbar>
      );
  }

  function NavItem(props){
    const [open, setOpen] = useState(false);
    return(

      <a className="icon-button" onClick = {()=> setOpen(!open)}>
      {props.icon}

      {open && props.children}
      </a>
      )
    }

    return (
    <div className='AvatarDiv'>
    {App(this.state.avatar_link)}   
    </div>
    )
  }
}


export default Dropdown;
