import React from 'react';
import fire from './config/Fire';



class Home extends React.Component {

  logout() {
    fire.auth().signOut();
  }

getAvatarLink() {
	fire.auth().onAuthStateChanged((user) => {
        console.log(user.email);
        const userAvatarLink = fire.database().ref('users/' + user.uid + '/avatar_link');
        prompt (userAvatarLink);

    });

  };

setAvatarLink() {
	fire.auth().onAuthStateChanged((user) => {
        console.log(user.email);
        const userAvatarLink = prompt('Link to your avatar');
        fire.database().ref('users/' + user.uid).update({
        avatar_link: userAvatarLink
    });
    });

  };

componentDidMount(){
	fire.auth().onAuthStateChanged((user) => {
      if (user) {
        console.log(user.email);        
     } else {
        console.log('error!!!');
      }
    });
}



  render() {
    return (
      <div style={{textAlign: 'center'}}>
        <h1>You Are Logged In</h1>
        <img src="image" alt="Avatar" />;
        <button onClick = {this.logout}>Logout</button>
        <button onClick = {this.setAvatarLink}>Set Avatar</button>
        <button onClick = {this.userAvatarLink}>Get Avatar</button>
      </div>
    )
  }
}


export default Home;
