import React from 'react';
import fire from './config/Fire';
import {BrowserRouter, Route, NavLink} from 'react-router-dom';

class Signup extends React.Component {


signUp() {
    const email = document.querySelector('#email').value;
    const password = document.querySelector('#password').value;
    fire.auth().createUserWithEmailAndPassword(email, password)
    .then((u) => {
      console.log('Successfully Signed Up');
      const user = fire.auth().currentUser;
      const userId = user.uid;
      fire.database().ref('users/' + userId).set({
        email: user.email,
        user_uid: user.uid,
        avatar_link: "",
      }, (error) => {
        if (error) {
          console.log("Error!");
        } else {
          console.log(userId);
        }
      })
    })
    .catch((err) => {
    	this.setState({error: err.toString()});
      console.log('Error: ' + err.toString());
    })
  }

error() {
	return(
		<div>

		</div>
		)

}

	render() {
		return(

      <div className='signUp'>
      {this.state.error && (this.state.error)}
      {this.state.error}
      <h3>Реєстрація</h3>
      <div>
      <div>Email</div>
      <input id="email" placeholder="Ваш Email.." type="text"/>
      </div>
      <div>
      <div>Password</div>
      <input id="password" placeholder="Ваш пароль.." type="password"/>
      </div>
      <NavLink to='/login'><button style={{margin: '10px'}}>Увійти</button></NavLink>
      <button style={{margin: '10px'}} onClick={this.signUp}>Зареєструватися</button>

      </div>
			)
	}
}

export default Signup;
