import React, { useState } from 'react';
import fire from './config/Fire';
import Login from './Login.js';
import axios from 'axios';
import {BrowserRouter, Switch, Route, Link } from "react-router-dom";
import './App.css';
import Modal from 'react-modal';
import ModalFunc from './Modal.js';
import TestModal from './TestModal.js';
import { Redirect } from 'react-router';




const api = axios.create({
	baseURL: 'https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=mN6Xg33Uh049lVc0uwErmOgdhb4TYBt4'
})

Modal.setAppElement('#root');

class Articles extends React.Component {

	state = {
		user: null,
		articles: [],
		title: null,
		url: null,
		modalIsOpen: false,
		link: '',
	}

	constructor(props){
		super(props)
		this.getArticle = this.getArticle.bind(this);
		this.authListener = this.authListener.bind(this);
		this.ArticlesBody = this.ArticlesBody.bind(this);
		this.Post = this.Post.bind(this);
	}

	componentDidMount(){
		api.get().then(res => {
			this.setState({ articles: res.data.results,})
		})
		this.authListener();
	}

closeModal = (value) => {
   this.setState({ link: value })
}
	authListener() {
		fire.auth().onAuthStateChanged((user) => {
			if (user) {
				this.setState({ user });
			} else {
				this.setState({ user: null });
			}
		})
	}
// {this.getArticle()}
ArticlesBody() {
	return (
		<div className="Content">
		<div className="ContentRaw">{this.getArticle()}</div>
		</div>
		)
}
getArticle(){ 
    // Get article info


    const divStyle = {
    	width: '25%',
    	textDecoration: 'none',
    	borderBottom: '1px solid black',
    	padding: '0px 15px 15px 20px',
    	margin: '5px',
    	fontFamily: 'Domine'
    };

    const mediaArr = this.state.articles.map(article => 
    	article.media[0] ? 
    	(<div style={divStyle} key={article.title} className="ArticleDiv">
    		<a style={{ color: 'black' }} onClick={
    			() => this.state.user ? (this.setState({link: '/post', title: article.title, url: article.media[0]['media-metadata'][1]['url']})) :
    			 (this.setState({link: '/pleaselogin'}))

    		}>
    			{article.title}</a>
    			</div>) : (console.log("ХЗ почему я тут")) );
    			mediaArr.map(content => <div>{content}</div>);
    			return mediaArr;
    		}

    		Post () {
    			return (
    			<div>
    			<p>{this.state.title}</p>
    			<p>{this.state.url}</p>
    			<button onClick={this.setState({link: ''})}>Close</button>
    			</div>);
    		}



    		render() {
    			const xxx = () => {return (<h1>TEST</h1>)}
    			return (
    			<div className="ArticlePage">
    			{this.state.link == '/pleaselogin' ? (<TestModal closeModal={this.closeModal}/>) : (console.log(this.state.link))}
    			{this.state.link == '/post' ? (this.Post()) : (console.log(this.state.link))}
    			{this.ArticlesBody()}
    			</div>
    			)
    		}
    	}

    	export default Articles;
