import React, { useState } from 'react';
import fire from './config/Fire';
import Login from './Login.js';
import axios from 'axios';
import {BrowserRouter, Route, NavLink} from 'react-router-dom';
import Modal from './Modal.js';



const api = axios.create({
	baseURL: 'https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=mN6Xg33Uh049lVc0uwErmOgdhb4TYBt4'
})

class Articles extends React.Component {

	state = {
		user: null,
		articles: [],
		title: null,
		description: null
	}

	constructor(props){
		super(props)
		this.getArticle = this.getArticle.bind(this);
		this.authListener = this.authListener.bind(this);

	}

	componentDidMount(){
		api.get().then(res => {
			this.setState({ articles: res.data.results,})
		})
		this.authListener();
		const [modalActive, setModalActive] = useState(true);
	}

	authListener() {
		fire.auth().onAuthStateChanged((user) => {
			if (user) {
				this.setState({ user });
			} else {
				this.setState({ user: null });
			}
		})
	}

	getArticle(){ 
    // Get article titles


    const divStyle = {
    	textDecoration: 'none',
    	borderBottom: '1px solid black',
    	padding: '0px 15px 15px 20px',
    	margin: '5px',
    	fontFamily: 'Domine'
    };

    const mediaArr = this.state.articles.map(article => 
    	<div style={divStyle} key={article.title} className="ArticleDiv">
    	<a  style={{ color: 'black' }} onClick={
    		this.state.user ? (() => this.setState({title: article.title, description: article.media[0]['media-metadata'][1]['url']})) :
    		(() => this.setState({title: "Null", description: "Null"}))
    	}>
    	{article.title}</a></div>);
    return mediaArr;
}



render() {

	return (

		<div className="ArticlePage">{this.state.title ? (
			<div>
			<p>{this.state.title}</p>
			<p>{this.state.description}</p>
			</div>
			) : (
			<BrowserRouter>
			<div className="Content">
			<div className="ContentRaw">{this.getArticle().slice(0, 3)}</div>
			<div className="ContentRaw">{this.getArticle().slice(3, 6)}</div>
			<div className="ContentRaw">{this.getArticle().slice(6, 9)}</div>
			<div className="ContentRaw">{this.getArticle().slice(9, 12)}</div>
			<Modal active={modalActive} setActive ={setModalActive} />
			</div>
			</BrowserRouter>
			) }
			</div>

			)
	}
	}



export default Articles;


