import React, { useState } from 'react';
import './App.css';
import Modal from 'react-modal';

function ModalFunc(){
const [modalIsOpen, setModalIsOpen] = useState(true);
	return(
		<div>
		<Modal overlayClassName='Modal' isOpen={modalIsOpen} onRequestClose = {() => setModalIsOpen(false).then( ()=> setModalIsOpen(true))}>
		<h2>Залогіньтеся для детального огляду статей</h2>
		<p>Modal body</p>
		</Modal>
		</div>
		)
}

export default ModalFunc;
