import React, { useState } from 'react';
import fire from './config/Fire';
import Login from './Login.js';
import axios from 'axios';
import {BrowserRouter, Route, Link } from "react-router-dom";
import './App.css';
import Modal from 'react-modal';
import TestModal from './TestModal.js';

Modal.setAppElement('#root');
const api = axios.create({
	baseURL: 'https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=mN6Xg33Uh049lVc0uwErmOgdhb4TYBt4'
})

Modal.setAppElement('#root');

class Articles extends React.Component {

	state = {
		user: null,
		articles: [],
		title: null,
		url: null,
		modalIsOpen: false,
		link: '',
	}

	constructor(props){
		super(props)
		this.getArticle = this.getArticle.bind(this);
		this.authListener = this.authListener.bind(this);
		this.ArticlesBody = this.ArticlesBody.bind(this);
		this.Post = this.Post.bind(this);
	}

	componentDidMount(){
		api.get().then(res => {
			this.setState({ articles: res.data.results,})
		})
		this.authListener();
	}

	closeModal = (value) => {
		this.setState({ link: value })
	}
	authListener() {
		fire.auth().onAuthStateChanged((user) => {
			if (user) {
				this.setState({ user });
			} else {
				this.setState({ user: null });
			}
		})
	}
// {this.getArticle()}
ArticlesBody() {
	return (
		<div className="Content">
		<div className="ContentRaw">{this.getArticle()}</div>
		</div>
		)
}
getArticle(){ 
    // Get article info


    const mediaArr = this.state.articles.map(article => 
    	//const imgUrl = article.media[0]['media-metadata'][1]['url']
     	article.media[0] ? 
//     	( {const divStyle = {
// //backgroundImage: 'url(${})'
//     };}

    		(
    		//	const imgUrl = article.media[0]['media-metadata'][1]['url']

//backgroundImage: 'url(${article.media[0][\'media-metadata\'][1][\'url\']})'
    			<div style={{backgroundImage: 'url(${article.media[0][\'media-metadata\'][1][\'url\']})'}} key={article.title}>
    		<a style={{ color: 'black' }} onClick={
    			() => this.state.user ? (

    				this.setState({
    				link: '/post', 
    				article_url: article.url, 
    				title: article.title, 
    				url: article.media[0]['media-metadata'][1]['url']})) :
    			(this.setState({link: '/pleaselogin'}))

    		}>
    		{article.title}</a>
    		</div>) : (console.log("ХЗ почему я тут")) );
    mediaArr.map(content => <div>{content}</div>);
    	return mediaArr;
    }

    Post () {
    	return (
    	<Modal className='ModalWindow' isOpen='true'>
    	<div className = 'Modal_content'>
    	<p>{this.state.title}</p>
    	<img src={this.state.url}></img>
    	<p><a href={this.state.article_url}>Оригінал статті</a></p>
    	<p><button onClick={()=> this.setState({link: ''})}>Close</button></p>
    	</div>
    	</Modal>
    	);
    }



    render() {
    	return (
    	<div className="ArticlePage">
    	{this.state.link == '/pleaselogin' ? (<TestModal closeModal={this.closeModal}/>) : (console.log(this.state.link))}
    	{this.state.link == '/post' ? (this.Post()) : (console.log(this.state.link))}
    	{this.ArticlesBody()}
    	</div>
    	)
    }
}

export default Articles;
