  render() {

    return (
<div>
<Modal className='ModalWindow' isOpen={this.state.modalIsOpen}>
<h2>Title</h2>
<p>body</p>
<button onClick={() => this.setState({modalIsOpen: false})}>Close modal</button>
</Modal>
</div>
    );
  }
}

export default TestModal;
