import React from 'react';
import fire from './config/Fire';
import Articles from './Articles.js';
import {BrowserRouter, Route, NavLink} from 'react-router-dom';
//<button style={{margin: '10px'}} onClick={this.signUp}>Зареєструватися</button>
class Login extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      userAvatarLink: "",
    };
    this.Component = this.Component.bind(this);
  }

  signUp() {
    const email = document.querySelector('#email').value;
    const password = document.querySelector('#password').value;
    fire.auth().createUserWithEmailAndPassword(email, password)
    .then((u) => {
      console.log('Successfully Signed Up');
      const user = fire.auth().currentUser;
      const userId = user.uid;
      fire.database().ref('users/' + userId).set({
        email: user.email,
        user_uid: user.uid,
        avatar_link: ""
      }, (error) => {
        if (error) {
          console.log("Error!");
        } else {
          console.log(userId);
        }
      })
    })
    .catch((err) => {
      console.log('Error: ' + err.toString());
    })
  }


  login() {
    const email = document.querySelector('#email').value;
    const password = document.querySelector('#password').value;
    fire.auth().signInWithEmailAndPassword(email, password)
    .then((u) => {
      console.log('Successfully Logged In.');
    })
    .catch((err) => {
      console.log('Error: ' + err.toString());
    })
  }

Component() {
return (
      <div>
      <div>Email</div>
      <div><input id="email" placeholder="Ваш Email.." type="text"/></div>
      <div>Password</div>
      <div><input id="password" placeholder="Ваш пароль.." type="text"/></div>
      </div>
  )
}

  render() {
    return (
      <div className='AvatarDiv'>
      <h3>Авторизація</h3>
{this.Component()}
      <button style={{margin: '10px'}} onClick={this.login}>Увійти</button>
      <NavLink to='/signup'><button style={{margin: '10px'}}>Зареєструватися</button></NavLink>
      <BrowserRouter>
      <Route path='/signup' exact component={this.Component}/>
      </BrowserRouter>
      </div>
      )
  }
}

export default Login;
