import React from 'react';
import fire from './config/Fire';
import Login from './Login.js';
import axios from 'axios';
import {BrowserRouter, Route, Link } from "react-router-dom";
import './App.css';
import ReactModal from 'react-modal';
import ArticleModal from './ArticleModal.js';

ReactModal.setAppElement('#root');
const api = axios.create({
	baseURL: 'https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=mN6Xg33Uh049lVc0uwErmOgdhb4TYBt4'
})

class Articles extends React.Component {

	state = {
		user: null,
		articles: [],
		title: null,
		url: null,
		modalIsOpen: false,
		link: '',
		description: '',
		date: '',
        showModal: false
	}

	constructor(props){
		super(props)
		this.getArticle = this.getArticle.bind(this);
		this.authListener = this.authListener.bind(this);
		this.ArticlesBody = this.ArticlesBody.bind(this);
        this.setModalClose = this.setModalClose.bind(this);
		this.Post = this.Post.bind(this);
	}

	componentDidMount(){
		api.get().then(res => {
			this.setState({ articles: res.data.results,})
		})
		this.authListener();
	}

	authListener() {
		fire.auth().onAuthStateChanged((user) => {
			if (user) {
				this.setState({ user });
			} else {
				this.setState({ user: null });
			}
		})
	}
// {this.getArticle()}
ArticlesBody() {
	return (
		<div className="Content">
		<div className="ContentRaw">{this.getArticle()}</div>
		</div>
		)
}


setModalClose() {
    this.setState({showModal: false});
}

getArticle(){ 
    // Get article info


    const mediaArr = this.state.articles.map(article => 
    	article.media[0] ? 
    	(
    		<div className = 'ArticleDiv' style={{backgroundImage: 'url(' + article.media[0]['media-metadata'][2]['url'] + ')' }} key={article.title}>
    		<div className = 'textAlign'><a onClick={
    			() => this.state.user ? (
    				this.setState({
    					link: '/post', 
    					article_url: article.url, 
    					title: article.title, 
    					description: article.abstract,
    					date: article.published_date,
                        showModal: true,
    					url: article.media[0]['media-metadata'][2]['url']})) :
    			(this.setState({link: '/pleaselogin'}))

    		}>
    		{article.title}</a></div>
    		</div>) : (console.log("ХЗ почему я тут")) );
    		mediaArr.map(content => <div>{content}</div>);
    		return mediaArr;
    	}

    	Post () {
    		return (

    		<ReactModal className='ModalWindow' isOpen={this.state.showModal} onRequestClose={this.setModalClose}>
    		<div className = 'Modal_content'>
    		<div className='ArticleTitle'>{this.state.title}</div>
    		<img src={this.state.url}></img>
    		<div><a className='ArticleInfo'>{this.state.description}</a></div>
    		<div><a className='ArticleInfo'>Дата пулікації: {this.state.date}</a></div>
    		<div style={{display: 'flex', justifyContent: 'space-between'}}><a className='ArticleInfo' href={this.state.article_url}>Оригінал статті</a>
    		<button onClick={()=> this.setState({link: ''})}>На головну</button></div>
    		</div>
    		</ReactModal>
    		);
    	}



    	render() {
    		return (
    		<div className="ArticlePage">
    		{this.state.link == '/pleaselogin' ? (<ArticleModal closeModal={this.closeModal}/>) : (console.log(this.state.link))}
    		{this.state.link == '/post' ? (this.Post()) : (console.log(this.state.link))}
    		{this.ArticlesBody()}
    		</div>
    		)
    	}
    }

    export default Articles;
