  constructor(props) {
    super(props);
    this.setAvatarLink = this.setAvatarLink.bind(this);
    this.pathToAvatar = this.pathToAvatar.bind(this);
    this.state = {
    avatar_link: this.databaseAvatarLink
    };

}

componnentDidMount() {
this.pathToAvatar();
}

