        };



 render() {

    return (
      <div style={{textAlign: 'center'}}>
      this.pathToAvatar();
        <h3>You Are Logged In</h3>
        <img src= {this.state.avatar_link} />
        <button onClick = {this.logout}>Logout</button>
        <button onClick = {this.setAvatarLink}>Set Avatar</button>
        <button onClick = {this.pathToAvatar}> Show Avatar</button>
      </div>
    )
  }
}


export default Profile;
