import React, { useState } from 'react';
import Login from './Login.js';
import axios from 'axios';
import {BrowserRouter, Route, NavLink} from 'react-router-dom';

const api = axios.create({
	baseURL: 'https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=mN6Xg33Uh049lVc0uwErmOgdhb4TYBt4'
})

class Articles extends React.Component {

	state = {
		user: null,
		articles: [],
		title: null,
		description: null
	}

	authListener() {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user });
      } else {
        this.setState({ user: null });
      }
    })
  }

	constructor(props){
		super(props)
		this.getArticle = this.getArticle.bind(this);
		this.authListener = this.authListener.bind(this);

	}

	componentDidMount(){
		api.get().then(res => {
			this.setState({ articles: res.data.results,})
		})
		    this.authListener();
	}

	getArticle(){ 
    // Get article titles


    const divStyle = {
    	textDecoration: 'none',
    	borderBottom: '1px solid black',
    	padding: '15px',
    	margin: '15px',
    	fontFamily: 'Domine'
    };

    const mediaArr = this.state.articles.map(article => 
    	<div style={divStyle} key={article.title} id="ArticleDiv">
    	<a  style={{ color: 'black' }} onClick={
    		this.state.user ? (() => this.setState({title: article.title, description: article.abstract})) :
    		 (() => this.setState({title: "Null", description: "Null"}))
    	}>
    	{article.title}</a></div>);
    return mediaArr;
}

render() {

	return (
		<div>{this.state.title ? (
			<div>
			<p>{this.state.title}</p>
			<p>{this.state.description}</p>
			</div>
			) : (
			<BrowserRouter>
			<div className="Content">
			<div className="ContentRaw">{this.getArticle().slice(0, 4)}</div>
			<div className="ContentRaw">{this.getArticle().slice(4, 8)}</div>
			<div className="ContentRaw">{this.getArticle().slice(8, 12)}</div>
			</div>
			</BrowserRouter>
			) }
		</div>

			)
	}

}

export default Articles;


