  return (<h1><Login /></h1>);}
} />


    </BrowserRouter>
    )
}

  render() {
    return (
      <BrowserRouter>
         <div className="App">
            <div className="Header">
                {<Header/>}
                    { this.state.user ? ( <Home /> ) : ( this.loginPath() ) }
                         </div>
                    <Route component = {<Articles />} />

                 </div>
       </BrowserRouter>
    );
  }
}

export default App;
