import React from 'react';
import fire from './config/Fire';
import { createBrowserHistory } from "history";
import { BrowserRouter, Router, Route, Switch, NavLink } from 'react-router-dom';
import Profile  from './Profile.js'


class LoginForm extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      error: ''
    }
    this.loginFunc = this.loginFunc.bind(this);
    this.test = this.test.bind(this);
    this.authListener = this.authListener.bind(this);
    this.loginForm = this.loginForm.bind(this);
    this.loginForm();
  }

  authListener() {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user });
      } else {
        this.setState({ user: null });
      }
    })
  }

    componentDidMount() {
    this.authListener();
    this.loginForm();
  }

  loginFunc() {
    const email = document.querySelector('#email').value;
    const password = document.querySelector('#password').value;
    fire.auth().signInWithEmailAndPassword(email, password)
    .then((u) => {
      console.log('Successfully Logged In.');
    })
    .catch((err) => {
      this.setState({error: err.toString()});
    })
    this.test();
  }

  test(){
    const customHistory = createBrowserHistory();
    customHistory.push('/profile');
    <Router history={customHistory} />
  }

  loginForm() {
return (
    <div className='AvatarDiv'>
    <h3>Авторизація</h3>
    <div>
    <div>Email</div>
    <input id="email" placeholder="Ваш Email.." type="text"/>
    </div>
    <div>
    <div>Password</div>
    <input id="password" placeholder="Ваш пароль.." type="password"/>
    </div>
    <div style = {{color: 'red'}}>{this.state.error}</div>
    <button style={{margin: '10px'}} onClick={this.loginFunc}>Увійти</button>
    <NavLink to='/signup'><button style={{margin: '10px'}}>Зареєструватися</button></NavLink>
    <button onClick={this.test}>Test</button>
    </div>
  )
  }


  render(){

   return(
    <div>
    {this.loginForm()}
    </div>
    )
 }
}

export default LoginForm;
