  componentDidMount() {
    this.authListener();
  }

  authListener() {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user });
      } else {
        this.setState({ user: null });
      }
    })
  }

  render() {
    return (
      <div className="App">
      { <Header /> }
      <div>
      { this.state.user ? ( <Home /> ) : ( <Login /> ) }
      {<Articles />}
      </div>
      </div>
    );
  }
}

export default App;
