import React, { Component } from 'react';
import fire from './config/Fire'
import Login from './Login.js';
import Home from './Home.js';
import Header from './Header.js';
import Articles from './Articles.js';
import {BrowserRouter, Route} from 'react-router-dom';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      user: null,
    };

    this.authListener = this.authListener.bind(this);
    this.loginPath = this.loginPath.bind(this);
  }



  componentDidMount() {
    this.authListener();
  }

  authListener() {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user });
      } else {
        this.setState({ user: null });
      }
    })
  }

loginPath(){
  return (
    <BrowserRouter>
<Route path='/login.js' component={Login}/></BrowserRouter>
    )
}

  render() {
    return (

      <div className="App">
      <div className="Header">
      { <Header /> }
      { this.state.user ? ( <Home /> ) : ( <button style={{margin: '10px'}} href={this.loginPath()}>Login</button> ) }
      </div>

        {<Articles />}

      </div>
    );
  }
}

export default App;
