import React, { useState } from 'react';
import fire from './config/Fire';
import Login from './Login.js';
import axios from 'axios';
import {BrowserRouter, Route, NavLink} from 'react-router-dom';
import './App.css';
import Modal from 'react-modal';
import ModalFunc from './Modal.js'
import Post from './Post.js'



const api = axios.create({
	baseURL: 'https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=mN6Xg33Uh049lVc0uwErmOgdhb4TYBt4'
})

Modal.setAppElement('#root');

class Articles extends React.Component {

	state = {
		user: null,
		articles: [],
		title: null,
		url: null,
		modalIsOpen: false,
		testTitle: "TESTTITLE"
	}

	closeModal = (value) => {
		this.setState({modalIsOpen: value})
	}

	constructor(props){
		super(props)
		this.getArticle = this.getArticle.bind(this);
		this.authListener = this.authListener.bind(this);
		this.ArticlesBody = this.ArticlesBody.bind(this);
		this.Post = this.Post.bind(this);
	}

	componentDidMount(){
		api.get().then(res => {
			this.setState({ articles: res.data.results,})
		})
		this.authListener();
	}

	authListener() {
		fire.auth().onAuthStateChanged((user) => {
			if (user) {
				this.setState({ user });
			} else {
				this.setState({ user: null });
			}
		})
	}

	getArticle(){ 
    // Get article info


    const divStyle = {
    	width: '25%',
    	textDecoration: 'none',
    	borderBottom: '1px solid black',
    	padding: '0px 15px 15px 20px',
    	margin: '5px',
    	fontFamily: 'Domine'
    };

    const mediaArr = this.state.articles.map(article => 
    	article.media[0] ? 
    	(<div style={divStyle} key={article.title} className="ArticleDiv">
    		<NavLink to= '/post'><a style={{ color: 'black' }} onClick={
    			() => {this.setState({title: article.title, url: article.media[0]['media-metadata'][1]['url']})}
    		}>
    		{article.title}</a></NavLink></div>) : (console.log("Null")) );

    		mediaArr.map(content => <div>{content}</div>);
    		return mediaArr;
    	}

    	ArticlesBody(){
    		return (
    		<div className="Content">
    		<div className="ContentRaw">{this.getArticle()}</div>
    		</div>
    		)
    	}

    	Post(props){
    		return (this.state.user ? (
    		    		<div>
    		    		<p>{this.props.title}</p>
    		    		<p>{this.state.url}</p>
    		    		</div>) :
    		    		(<div>Юзера нет!</div>))
    	}

    	render() {

    		return (
    		<BrowserRouter>
    		<div className="ArticlePage">
    		<Route path='/' exact={true} component = {this.ArticlesBody}/>
    		<Route path='/post' exact={true} component = {this.Post}  title={this.state.title}/>
    		</div>
    		</BrowserRouter>
    		)
    	}
    }



    export default Articles;
