import React, { useState } from 'react';
import fire from './config/Fire';
import Login from './Login.js';
import axios from 'axios';
import {BrowserRouter, Route, Link } from "react-router-dom";
import './App.css';
import Modal from 'react-modal';
import TestModal from './TestModal.js';

const testGlobal = 'https://www.google.com/logos/doodles/2020/december-holidays-days-2-30-6753651837108830.5-s.png';

Modal.setAppElement('#root');
const api = axios.create({
	baseURL: 'https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=mN6Xg33Uh049lVc0uwErmOgdhb4TYBt4'
})

Modal.setAppElement('#root');

class Articles extends React.Component {

	state = {
		user: null,
		articles: [],
		title: null,
		url: null,
		modalIsOpen: false,
		link: '',
		description: '',
		date: '',
        showModal: false
	}

	constructor(props){
		super(props)
		this.getArticle = this.getArticle.bind(this);
		this.authListener = this.authListener.bind(this);
		this.ArticlesBody = this.ArticlesBody.bind(this);
        this.setModalIsOpen = this.setModalIsOpen.bind(this);
		this.Post = this.Post.bind(this);
	}

	componentDidMount(){
		api.get().then(res => {
			this.setState({ articles: res.data.results,})
		})
		this.authListener();
	}

	closeModal = (value) => {
		this.setState({ link: value })
	}
	authListener() {
		fire.auth().onAuthStateChanged((user) => {
			if (user) {
				this.setState({ user });
			} else {
				this.setState({ user: null });
			}
		})
	}
// {this.getArticle()}
ArticlesBody() {
	return (
		<div className="Content">
		<div className="ContentRaw">{this.getArticle()}</div>
		</div>
		)
}

setModalIsOpen() {
    this.setState({showModal: false});
}

getArticle(){ 
    // Get article info


    const mediaArr = this.state.articles.map(article => 
    	article.media[0] ? 
    	(
    		<div className = 'ArticleDiv' style={{backgroundImage: 'url(' + article.media[0]['media-metadata'][2]['url'] + ')' }} key={article.title}>
    		<div className = 'textAlign'><a onClick={
    			() => this.state.user ? (

    				this.setState({
    					link: '/post', 
    					article_url: article.url, 
    					title: article.title, 
    					description: article.abstract,
    					date: article.published_date,
    					url: article.media[0]['media-metadata'][2]['url']})) :
    			(this.setState({link: '/pleaselogin'}))

    		}>
    		{article.title}</a></div>
    		</div>) : (console.log("ХЗ почему я тут")) );
    		mediaArr.map(content => <div>{content}</div>);
    		return mediaArr;
    	}

    	Post () {
    		return (
    		<Modal className='ModalWindow' isOpen={this.state.showModal} onRequestClose={this.setModalIsOpen}>
    		<div className = 'Modal_content'>
    		<div className='ArticleTitle'>{this.state.title}</div>
    		<img src={this.state.url}></img>
    		<div><a className='ArticleInfo'>{this.state.description}</a></div>
    		<div><a className='ArticleInfo'>Дата пулікації: {this.state.date}</a></div>
    		<div><a className='ArticleInfo' href={this.state.article_url}>Оригінал статті</a></div>
    		<div className='ArticleInfo'><button onClick={()=> this.setState({link: ''})}>На головну</button></div>
    		</div>
    		</Modal>
    		);
    	}



    	render() {
    		return (
    		<div className="ArticlePage">
    		{this.state.link == '/pleaselogin' ? (<TestModal closeModal={this.closeModal}/>) : (console.log(this.state.link))}
    		{this.state.link == '/post' ? (this.Post()) : (console.log(this.state.link))}
    		{this.ArticlesBody()}
    		</div>
    		)
    	}
    }

    export default Articles;
