import React, { useState } from 'react';
import axios from 'axios';
import {BrowserRouter, Route, NavLink} from 'react-router-dom';

const api = axios.create({
	baseURL: 'https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=mN6Xg33Uh049lVc0uwErmOgdhb4TYBt4'
})

class Articles extends React.Component {

	state = {
		articles: [],
	}


	constructor(props){
		super(props)
		this.getArticle = this.getArticle.bind(this);

	}

	componentDidMount(){
		api.get().then(res => {
			this.setState({ articles: res.data.results,})
		})
	}

	getArticle(){ 
    // Get article titles


    const divStyle = {
    	textDecoration: 'none',
    	borderBottom: '1px solid black',
    	padding: '15px',
    	margin: '15px',
    	fontFamily: 'Domine'
    };

    const mediaArr = this.state.articles.map(media => 
    	<div style={divStyle} key={media.title} id="ArticleDiv">
    	<a  style={{ color: 'black' }} onClick={this.setState({user: ""})}>
    	{media.title}</a></div>);
    return mediaArr;
}

render() {

	return (

		<BrowserRouter>
		<div className="Content">
		<div className="ContentRaw">{this.getArticle().slice(0, 4)}</div>
		<div className="ContentRaw">{this.getArticle().slice(4, 8)}</div>
		<div className="ContentRaw">{this.getArticle().slice(8, 12)}</div>
		</div>
		</BrowserRouter>
		)
	}

}

export default Articles;


