import React from 'react';
import fire from './config/Fire';

class Profile extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
    avatar_link: '',
    user_name: '', 
    };
    this.nameAndAvatar =this.nameAndAvatar.bind(this);
}

componentDidMount() {
this.nameAndAvatar();
}

  logout() {
    fire.auth().signOut();
  }

//ДОБАВИТЬ УСЛОВИЕ: ЕСЛИ В БАЗЕ ЕССТЬ ССЫЛКА, ВСТАВЛЯТЬ ЕЕ, ЕСЛИ НЕТ ТО ВСТАВЛЯТЬ СТАНДАРТНУЮ
nameAndAvatar(){
const userId = fire.auth().currentUser.uid;
const AvaLink = fire.database().ref('/users/' + userId).once('value').then((snapshot) => {
const databaseAvatarLink = (snapshot.val() && snapshot.val().avatar_link) || "Error!";
const databaseName = (snapshot.val() && snapshot.val().user_name) || "Error!";
this.setState({avatar_link: databaseAvatarLink.toString()}); 
this.setState({user_name: databaseName.toString()});
console.log(databaseAvatarLink.toString());
console.log(databaseName.toString());
});
}

setAvatarLink() {
	//fire.auth().onAuthStateChanged((user) => {
        const uid = fire.auth().currentUser.uid;
        const userAvatarLink = prompt('Link to your avatar');
        userAvatarLink ?  (fire.database().ref('users/' + uid).update({avatar_link: userAvatarLink})) : (console.log("NULL!!"))
        this.pathToAvatar();      
        };

ChangeName() {
    //fire.auth().onAuthStateChanged((user) => {
        const uid = fire.auth().currentUser.uid;
        const email = fire.auth().currentUser.email;
        const userName = prompt('Your name');
        userName ?  (fire.database().ref('users/' + uid).update({user_name: userName})) : (console.log("NULL!!"))
        this.pathToName();      
        };

TestLink(){
    const link = 'https://developer.mozilla.org/ru/';
    return link;
}

 render() {

    return (
      <div style={{textAlign: 'center'}}>
        <h1>Привіт, {this.state.user_name}</h1>
        <img style = {{maxHeight: '10vw'}} src= {this.state.avatar_link} />
        <button onClick = {this.logout}>Logout</button>
        <button onClick = {this.setAvatarLink.bind(this)}>Set Avatar</button>
        <button onClick = {this.ChangeName.bind(this)}> Change name</button>
      </div>
    )
  }
}


export default Profile;
