import fire from './config/Fire';

class Home extends React.Component {

  logout() {
    fire.auth().signOut();
  }

getAvatarLink() {
fire.auth().onAuthStateChanged((user) => {
const userId = fire.auth().currentUser.uid;
const userLink = fire.database().ref('/users/' + userId).once('value').then((snapshot) => {
  const userAvatarLink = (snapshot.val() && snapshot.val().avatar_link) || "Error!";
});
    return userLink;});
};

alertAvatarLink () {
	const testLink = "https://aif-s3.aif.ru/images/018/688/6c24ad3babc2ecc3d1286ae7454698fc.jpg";
	return testLink;
}

setAvatarLink() {
	fire.auth().onAuthStateChanged((user) => {
        console.log(user.email);
        const userAvatarLink = prompt('Link to your avatar');
        fire.database().ref('users/' + user.uid).update({
        avatar_link: userAvatarLink
    });
    });

  };

componentDidMount(){
	fire.auth().onAuthStateChanged((user) => {
      if (user) {
        console.log(user.email);        
     } else {
        console.log('error!!!');
      }
    });
}


  render() {
    return (
      <div style={{textAlign: 'center'}}>
        <h1>You Are Logged In</h1>
        <img src= {this.getAvatarLink()} />
        <button onClick = {this.logout}>Logout</button>
        <button onClick = {this.setAvatarLink}>Set Avatar</button>
        <button onClick = {this.getAvatarLink}>Get Avatar</button>


        <button onClick = {this.alertAvatarLink}> Alert avatarLink</button>
      </div>
    )
  }
}


export default Home;
